import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";  // Componente de Login
import Panel from "./Panel";  // Componente del Panel de usuario
import Register from "./Register";  // Componente de registro

// Protege el acceso a rutas privadas
const RutaPrivada = ({ children }) => {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Ruta pública para el login */}
        <Route path="/login" element={<Login />} />

        {/* Ruta pública para el registro */}
        <Route path="/register" element={<Register />} />

        {/* Ruta privada para acceder al Panel (solo si hay un token válido) */}
        <Route
          path="/panel"
          element={
            <RutaPrivada>
              <Panel />
            </RutaPrivada>
          }
        />

        {/* Cualquier otra ruta → redirige a login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
