import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Panel from "./Panel";

// üîê Componente para proteger rutas privadas
const RutaPrivada = ({ children }) => {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Ruta p√∫blica: login */}
        <Route path="/login" element={<Login onLoginSuccess={() => window.location.href = "/panel"} />} />


        {/* Ruta protegida: panel */}
        <Route
          path="/panel"
          element={
            <RutaPrivada>
              <Panel />
            </RutaPrivada>
          }
        />

        {/* Cualquier otra ruta ‚Üí redirige seg√∫n autenticaci√≥n */}
        <Route
          path="*"
          element={
            localStorage.getItem("token") ? (
              <Navigate to="/panel" />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
