// Archivo: src/api.js
import axios from "axios";

const API_URL = "http://localhost:3001/api";

// Axios instancia con token (si está presente)
const api = axios.create({
  baseURL: API_URL,
});

api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Funciones API
export const obtenerServidores = () => api.get("/servidores");
export const actualizarEstadoServidor = (id, estado) =>
  api.post(`/servidores/${id}/estado`, { estado });

export const loginUsuario = (email, password) =>
  api.post("/login", { email, password });

export const registrarUsuario = (email, password, rol) =>
  api.post("/register", { email, password, rol });
