import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";


function Panel() {
  const [servidores, setServidores] = useState([]);
  const navigate = useNavigate();

  const token = localStorage.getItem("token");
  const usuario = token ? jwtDecode(token) : null;
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/servidores", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setServidores(res.data))
      .catch((err) => console.error("Error al obtener servidores:", err));
  }, []);

  const cerrarSesion = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  // üîÅ Funci√≥n para simular reinicio
  const reiniciarServidor = (id) => {
    const nombreServidor = servidores.find((s) => s.id === id)?.nombre;

    setServidores((prev) =>
      prev.map((srv) =>
        srv.id === id ? { ...srv, estado: "Reiniciando..." } : srv
      )
    );

    // A√±adir evento de reinicio
    setEventos((prev) => [
      { hora: new Date().toLocaleTimeString(), mensaje: `üîÅ ${nombreServidor} se est√° reiniciando` },
      ...prev
    ]);

    setTimeout(() => {
      setServidores((prev) =>
        prev.map((srv) =>
          srv.id === id ? { ...srv, estado: "online" } : srv
        )
      );

      setEventos((prev) => [
        { hora: new Date().toLocaleTimeString(), mensaje: `‚úÖ ${nombreServidor} ha vuelto a estar online` },
        ...prev
      ]);
    }, 3000);
  };


  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <div className="flex justify-between items-start mb-6">
        <div>
          <h1 className="text-3xl font-bold text-blue-700 mb-1">
            Panel de Infraestructura IT
          </h1>
          {usuario && (
            <p className="text-sm text-gray-600">
              Bienvenido, <span className="font-semibold">{usuario.email}</span>
            </p>
          )}
        </div>

        <button
          onClick={cerrarSesion}
          className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded shadow"
        >
          Cerrar sesi√≥n
        </button>
      </div>

      {servidores.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {servidores.map((srv) => (
            <div
              key={srv.id}
              className="bg-white p-4 rounded-lg shadow border border-gray-200"
            >
              <h2 className="text-xl font-semibold mb-2">{srv.nombre}</h2>
              <p><strong>IP:</strong> {srv.ip}</p>
              <p>
                <strong>Estado:</strong>{" "}
                <span className={srv.estado === "online" ? "text-green-600" : "text-yellow-600"}>
                  {srv.estado || "desconocido"}
                </span>
              </p>
              <p><strong>CPU:</strong> {srv.cpu}</p>
              <p><strong>RAM:</strong> {srv.ram}</p>
              <div className="mt-2">
                <strong>Servicios:</strong>
                <ul className="list-disc list-inside text-gray-700">
                  {srv.servicios.map((s, i) => (
                    <li key={i}>{s}</li>
                  ))}
                </ul>
              </div>
              <button
                onClick={() => reiniciarServidor(srv.id)}
                className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
              >
                Reiniciar
              </button>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500 text-center">Cargando servidores...</p>
      )}
    </div>
  );
}

export default Panel;
// Aqu√≠ se muestra el panel de usuario con la lista de servidores
// y la opci√≥n de reiniciar cada uno. Tambi√©n se incluye un bot√≥n para cerrar sesi√≥n.
// El componente utiliza el hook useEffect para obtener la lista de servidores
// al cargar el componente y la funci√≥n reiniciarServidor para simular el reinicio de un servidor.
// Adem√°s, se utiliza el hook useNavigate de react-router-dom para redirigir al usuario
// a la p√°gina de inicio de sesi√≥n al cerrar sesi√≥n.
// El componente tambi√©n muestra un mensaje de bienvenida al usuario
// utilizando el token JWT decodificado para obtener su correo electr√≥nico.
// Se utiliza Tailwind CSS para el estilo de los componentes y la disposici√≥n de la p√°gina.
// El componente maneja el estado de los servidores y su visualizaci√≥n
// utilizando el hook useState, y se encarga de mostrar un mensaje de carga
// mientras se obtienen los datos de los servidores desde el backend.
