import React, { useEffect, useState } from "react";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import {
  getServidores,
  actualizarEstadoServidor,
  restablecerServidoresSimulados,
} from "./api";
import {
  getVpsRealStatus,
  encenderVpsReal,
  apagarVpsReal,
  reiniciarVpsReal,
} from "./hetzner";

function Panel() {
  const [servidores, setServidores] = useState([]);
  const [eventos, setEventos] = useState([]);
  const [loadingReset, setLoadingReset] = useState(false);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const usuario = token ? jwtDecode(token) : null;

  useEffect(() => {
    const fetchData = async () => {
      const servidoresGuardados = JSON.parse(localStorage.getItem("servidores"));
      if (servidoresGuardados && servidoresGuardados.length > 0) {
        setServidores(servidoresGuardados);
      } else {
        try {
          const [vpsReal, simulados] = await Promise.all([
            getVpsRealStatus(),
            getServidores(token),
          ]);
          const todos = [vpsReal, ...simulados];
          setServidores(todos);
          localStorage.setItem("servidores", JSON.stringify(todos));
        } catch (err) {
          console.error("Error al obtener servidores:", err);
        }
      }
    };

    fetchData();
    const eventosGuardados = JSON.parse(localStorage.getItem("eventos") || "[]");
    setEventos(eventosGuardados);
  }, []);

  const cerrarSesion = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const actualizarEventos = (nuevoEvento) => {
    const nuevosEventos = [
      { hora: new Date().toLocaleTimeString(), mensaje: nuevoEvento },
      ...eventos,
    ];
    setEventos(nuevosEventos);
    localStorage.setItem("eventos", JSON.stringify(nuevosEventos));
  };

  const actualizarServidores = (nuevosServidores) => {
    setServidores(nuevosServidores);
    localStorage.setItem("servidores", JSON.stringify(nuevosServidores));
  };

  const encenderServidor = async (id) => {
    const nombreServidor = servidores.find((s) => s.id === id)?.nombre;
    try {
      if (id === "real-hetzner") {
        await encenderVpsReal();
      } else {
        await actualizarEstadoServidor(id, "online", token);
      }
      localStorage.removeItem("servidores");
      const [vpsReal, simulados] = await Promise.all([
        getVpsRealStatus(),
        getServidores(token),
      ]);
      actualizarServidores([vpsReal, ...simulados]);
      actualizarEventos(`🟢 ${nombreServidor} ha sido encendido`);
      toast.success(`${nombreServidor} encendido correctamente`);
    } catch (err) {
      console.error("Error al encender servidor:", err);
      toast.error("No se pudo encender el servidor");
    }
  };

  const apagarServidor = async (id) => {
    const nombreServidor = servidores.find((s) => s.id === id)?.nombre;
    try {
      if (id === "real-hetzner") {
        await apagarVpsReal();
      } else {
        await actualizarEstadoServidor(id, "offline", token);
      }
      localStorage.removeItem("servidores");
      const [vpsReal, simulados] = await Promise.all([
        getVpsRealStatus(),
        getServidores(token),
      ]);
      actualizarServidores([vpsReal, ...simulados]);
      actualizarEventos(`🔴 ${nombreServidor} ha sido apagado`);
      toast.warn(`${nombreServidor} ha sido apagado`);
    } catch (err) {
      console.error("Error al apagar servidor:", err);
      toast.error("No se pudo apagar el servidor");
    }
  };

  const reiniciarServidor = async (id) => {
    const nombreServidor = servidores.find((s) => s.id === id)?.nombre;
    try {
      if (id === "real-hetzner") {
        await reiniciarVpsReal();
      } else {
        await actualizarEstadoServidor(id, "Reiniciando...", token);
      }
      const enReinicio = servidores.map((srv) =>
        srv.id === id ? { ...srv, estado: "Reiniciando..." } : srv
      );
      actualizarServidores(enReinicio);
      actualizarEventos(`🔁 ${nombreServidor} se está reiniciando`);
      toast.info(`${nombreServidor} se está reiniciando...`);

      setTimeout(async () => {
        const [vpsReal, simulados] = await Promise.all([
          getVpsRealStatus(),
          getServidores(token),
        ]);
        actualizarServidores([vpsReal, ...simulados]);
        actualizarEventos(`✅ ${nombreServidor} ha vuelto a estar online`);
        toast.success(`${nombreServidor} está online`);
      }, 3000);
    } catch (err) {
      console.error("Error al reiniciar servidor:", err);
      toast.error("Error al reiniciar el servidor");
    }
  };

  const restablecerServidores = async () => {
    if (!window.confirm("¿Estás seguro de que quieres restablecer todos los servidores y borrar el historial?")) {
      return;
    }

    setLoadingReset(true);
    try {
      const todos = await getServidores(token);
      const idsSimulados = todos.filter((s) => typeof s.id === "number").map((s) => s.id);
      await restablecerServidoresSimulados(token, idsSimulados);
      const final = await getServidores(token);
      const vpsReal = await getVpsRealStatus();
      actualizarServidores([vpsReal, ...final]);
      toast.success("Servidores restablecidos y online");
      setEventos([]);
      localStorage.removeItem("eventos");
      actualizarEventos("⚙️ Todos los servidores fueron restablecidos");
      toast.info("Historial de eventos borrado");
    } catch (err) {
      console.error("Error al restablecer servidores:", err);
      toast.error("Error al restablecer los servidores");
    } finally {
      setLoadingReset(false);
    }
  };

  return (
    // ... JSX igual que antes ...
    <></>
  );
}

export default Panel;
