import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import Panel from "./Panel";

// ðŸ” Componente para proteger rutas privadas
const RutaPrivada = ({ children }) => {
  const token = localStorage.getItem("token");
  return token ? children : <Navigate to="/login" />;
};

function App() {
  // ðŸ‘‡ Este es el estado que faltaba
  const [autenticado, setAutenticado] = useState(
    !!localStorage.getItem("token")
  );

  return (
    <Router>
      <Routes>
        {/* Ruta pÃºblica: login */}
        <Route
          path="/login"
          element={<Login onLoginSuccess={() => setAutenticado(true)} />}
        />

        {/* Ruta protegida: panel */}
        <Route
          path="/panel"
          element={
            <RutaPrivada>
              <Panel onLogout={() => setAutenticado(false)} />
            </RutaPrivada>
          }
        />

        {/* Cualquier otra ruta â†’ redirige segÃºn autenticaciÃ³n */}
        <Route
          path="*"
          element={
            autenticado ? (
              <Navigate to="/panel" />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
