// Importamos React y hooks necesarios
import React, { useEffect, useState } from "react";

// Importamos axios para realizar peticiones HTTP
import axios from "axios";

// Importamos iconos de react-icons (font awesome)
import {
  FaMicrochip,       // Icono CPU
  FaMemory,          // Icono RAM
  FaNetworkWired,    // Icono Red/IP
  FaSyncAlt          // Icono de "Reiniciar"
} from "react-icons/fa";

function App() {
  // Estado que contiene la lista de servidores
  const [servidores, setServidores] = useState([]);

  // useEffect se ejecuta al montar el componente (solo una vez)
  useEffect(() => {
    axios
      .get("http://172.22.180.134:3001/api/servidores") // Llamada al backend
      .then((res) => setServidores(res.data))          // Guardamos la respuesta en el estado
      .catch((err) => console.error(err));             // Manejamos errores en consola
  }, []);

  // Función auxiliar para dar color al texto del CPU según uso
  const obtenerColorCPU = (cpu) => {
    const porcentaje = parseInt(cpu.replace("%", "")); // Extraemos el número
    if (porcentaje < 40) return "text-green-600";       // Bajo consumo
    if (porcentaje < 80) return "text-yellow-500";      // Medio
    return "text-red-600";                              // Alto
  };

  // Simula una acción de reinicio de servidor (solo con alert)
  const reiniciarServidor = (nombre) => {
    axios
      .post("http://172.22.180.134:3001/api/reiniciar", { nombre })
      .then((res) => {
        alert(res.data.mensaje); // Muestra el mensaje del backend
      })
      .catch((err) => {
        console.error(err);
        alert("❌ Error al reiniciar el servidor");
      });
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Título del panel */}
        <h1 className="text-4xl font-bold text-center text-blue-700 mb-8">
          Panel de Infraestructura IT
        </h1>

        {/* Si ya se cargaron los servidores */}
        {servidores.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {servidores.map((srv) => (
              <div
                key={srv.id}
                className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition duration-300 flex flex-col justify-between"
              >
                {/* Contenido del servidor */}
                <div>
                  {/* Nombre del servidor */}
                  <h2 className="text-2xl font-bold text-gray-800 mb-2">{srv.nombre}</h2>

                  {/* Dirección IP */}
                  <p className="text-sm text-gray-500 flex items-center mb-1">
                    <FaNetworkWired className="mr-2 text-blue-500" />
                    <strong>IP:</strong>&nbsp;{srv.ip}
                  </p>

                  {/* Uso de CPU con color dinámico */}
                  <p className={`text-sm flex items-center mb-1 ${obtenerColorCPU(srv.cpu)}`}>
                    <FaMicrochip className="mr-2" />
                    <strong>CPU:</strong>&nbsp;{srv.cpu}
                  </p>

                  {/* RAM */}
                  <p className="text-sm text-purple-700 flex items-center mb-1">
                    <FaMemory className="mr-2" />
                    <strong>RAM:</strong>&nbsp;{srv.ram}
                  </p>

                  {/* Lista de servicios activos */}
                  <div className="mt-3">
                    <span className="block text-sm font-medium text-gray-600 mb-1">
                      Servicios:
                    </span>
                    <ul className="text-sm text-gray-700 list-disc list-inside space-y-1">
                      {srv.servicios.map((s, idx) => (
                        <li key={idx}>{s}</li>
                      ))}
                    </ul>
                  </div>
                </div>

                {/* Botón de acción: Reiniciar */}
                <button
                  onClick={() => reiniciarServidor(srv.nombre)}
                  className="mt-4 inline-flex items-center justify-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white text-sm font-semibold rounded-md shadow transition"
                >
                  <FaSyncAlt className="mr-2" /> Reiniciar
                </button>
              </div>
            ))}
          </div>
        ) : (
          // Mensaje si aún no hay datos
          <p className="text-center text-gray-500">Cargando servidores...</p>
        )}
      </div>
    </div>
  );
}

export default App;
