// Importamos React y el hook useState para manejar el estado del componente
import React, { useState } from "react";
// Importamos Axios para hacer peticiones HTTP al backend
import axios from "axios";

// Componente funcional Login
// Recibe una prop 'onLoginSuccess' que se ejecuta cuando el login es exitoso
function Login({ onLoginSuccess }) {
  // Estado para el correo electrónico del usuario
  const [email, setEmail] = useState("");
  // Estado para la contraseña del usuario
  const [password, setPassword] = useState("");
  // Estado para mostrar errores si las credenciales son incorrectas
  const [error, setError] = useState("");

  // Función que se ejecuta cuando se envía el formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevenimos el comportamiento por defecto del formulario (recargar la página)

    try {
      // Realizamos una petición POST al backend con los datos del login
      const res = await axios.post("http://localhost:3001/api/login", {
        email,
        password,
      });

      // Si el login es exitoso, guardamos el token en localStorage
      localStorage.setItem("token", res.data.token);
       onLoginSuccess();
      console.log("Token recibido:", res.data.token);

      // Ejecutamos la función que maneja el login exitoso (ej. redirigir al panel)
      onLoginSuccess();
    } catch (err) {
      // Si ocurre un error (credenciales incorrectas, etc.), mostramos mensaje de error
      setError("Credenciales incorrectas");
    }
  };

  // Render del formulario de login
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      {/* Formulario con estilos usando Tailwind CSS */}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md w-80">
        <h2 className="text-2xl font-bold mb-4 text-center">Iniciar sesión</h2>

        {/* Mostrar mensaje de error si existe */}
        {error && <p className="text-red-500 text-sm mb-3">{error}</p>}

        {/* Input para el correo electrónico */}
        <input
          type="email"
          placeholder="Correo"
          className="w-full mb-3 p-2 border rounded"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        {/* Input para la contraseña */}
        <input
          type="password"
          placeholder="Contraseña"
          className="w-full mb-3 p-2 border rounded"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {/* Botón para enviar el formulario */}
        <button className="bg-blue-600 hover:bg-blue-700 text-white font-semibold w-full p-2 rounded">
          Entrar
        </button>
      </form>
    </div>
  );
}

// Exportamos el componente para poder usarlo en otras partes de la app
export default Login;
