import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [servidores, setServidores] = useState([]);

  useEffect(() => {
    axios
      .get("http://172.22.180.134:3001/api/servidores")
      .then((res) => setServidores(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-center text-blue-700 mb-8">
          Panel de Infraestructura IT
        </h1>

        {servidores.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {servidores.map((srv) => (
              <div
                key={srv.id}
                className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition duration-300"
              >
                <h2 className="text-2xl font-bold text-gray-800 mb-2">
                  {srv.nombre}
                </h2>
                <p className="text-sm text-gray-500 mb-1">
                  <strong>IP:</strong> {srv.ip}
                </p>
                <p className="text-sm text-gray-500 mb-1">
                  <strong>CPU:</strong> {srv.cpu}
                </p>
                <p className="text-sm text-gray-500 mb-1">
                  <strong>RAM:</strong> {srv.ram}
                </p>
                <div className="mt-3">
                  <span className="block text-sm font-medium text-gray-600 mb-1">
                    Servicios:
                  </span>
                  <ul className="text-sm text-gray-700 list-disc list-inside space-y-1">
                    {srv.servicios.map((s, idx) => (
                      <li key={idx}>{s}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500">
            Cargando datos de los servidores...
          </p>
        )}
      </div>
    </div>
  );
}

export default App;
