// Importa React y hooks necesarios
import React, { useEffect, useState } from "react";

// Importa función para decodificar el token JWT
import { jwtDecode } from "jwt-decode";

// Hook para redirigir al usuario
import { useNavigate } from "react-router-dom";

// Biblioteca para mostrar notificaciones emergentes
import { toast } from "react-toastify";

// Funciones API centralizadas (buena práctica)
import {
  getServidores,
  encenderServidorAPI,
  apagarServidorAPI,
} from "./api";

// Componentes de gráfico circular
import { PieChart, Pie, Cell, Tooltip } from 'recharts';
