import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from "react-router-dom";
import Login from "./Login";
import Panel from "./Panel";

// Componente RutaPrivada que reacciona al cambio de token
const RutaPrivada = ({ children }) => {
  const token = localStorage.getItem("token");
  const location = useLocation();

  if (!token) {
    return <Navigate to="/login" replace state={{ from: location }} />;
  }

  return children;
};

function App() {
  const [autenticado, setAutenticado] = useState(!!localStorage.getItem("token"));

  useEffect(() => {
    const handleStorage = () => {
      setAutenticado(!!localStorage.getItem("token"));
    };

    window.addEventListener("storage", handleStorage);
    return () => window.removeEventListener("storage", handleStorage);
  }, []);

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={<Login onLoginSuccess={() => setAutenticado(true)} />}
        />

        <Route
          path="/panel"
          element={
            <RutaPrivada>
              <Panel onLogout={() => setAutenticado(false)} />
            </RutaPrivada>
          }
        />

        <Route
          path="*"
          element={
            autenticado ? <Navigate to="/panel" /> : <Navigate to="/login" />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
