import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [servidores, setServidores] = useState([]);

  useEffect(() => {
    axios
      .get("http://172.22.180.134:3001/api/servidores")
      .then((res) => setServidores(res.data))
      .catch((err) => console.error(err));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4">
      <div className="max-w-6xl mx-auto bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-3xl font-bold text-center text-blue-600 mb-6">
          Panel de Infraestructura IT
        </h1>
        {servidores.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="table-auto w-full text-left border border-gray-200 rounded">
              <thead className="bg-blue-100">
                <tr>
                  <th className="px-4 py-2">ID</th>
                  <th className="px-4 py-2">Nombre</th>
                  <th className="px-4 py-2">IP</th>
                  <th className="px-4 py-2">CPU</th>
                  <th className="px-4 py-2">RAM</th>
                  <th className="px-4 py-2">Servicios</th>
                </tr>
              </thead>
              <tbody>
                {servidores.map((srv) => (
                  <tr
                    key={srv.id}
                    className="border-t border-gray-200 hover:bg-gray-50"
                  >
                    <td className="px-4 py-2">{srv.id}</td>
                    <td className="px-4 py-2 font-semibold">{srv.nombre}</td>
                    <td className="px-4 py-2">{srv.ip}</td>
                    <td className="px-4 py-2">{srv.cpu}</td>
                    <td className="px-4 py-2">{srv.ram}</td>
                    <td className="px-4 py-2 text-sm text-gray-700">
                      {srv.servicios.join(", ")}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p className="text-center text-gray-500">
            Cargando datos de los servidores...
          </p>
        )}
      </div>
    </div>
  );
}

export default App;
