import React, { useEffect, useState } from "react";
import axios from "axios";

function Panel() {
  const [servidores, setServidores] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");

    axios
      .get("http://localhost:3001/api/servidores", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setServidores(res.data))
      .catch((err) => console.error(err));
  }, []);

    // Función auxiliar para dar color al texto del CPU según uso
  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Panel de Infraestructura IT</h1>
      {servidores.map((srv) => (
        <div key={srv.id} className="mb-4 border p-4 rounded bg-white shadow">
          <h2 className="text-xl font-semibold">{srv.nombre}</h2>
          <p><strong>IP:</strong> {srv.ip}</p>
          <p><strong>CPU:</strong> {srv.cpu}</p>
          <p><strong>RAM:</strong> {srv.ram}</p>
        </div>
      ))}

      {/* 🔴 Botón de cerrar sesión */}
      <button
        onClick={() => {
          localStorage.removeItem("token");
          window.location.reload();
        }}
        className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700 transition"
      >
        Cerrar sesión
      </button>
    </div>
  );


}

export default Panel;
