// hetzner.js
import axios from "axios";

const HETZNER_API_TOKEN = import.meta.env.VITE_HETZNER_API_TOKEN;
const HETZNER_API_URL = "https://api.hetzner.cloud/v1";
const SERVER_ID = "62522174";

const headers = {
  Authorization: `Bearer ${HETZNER_API_TOKEN}`,
};

export async function getVpsRealStatus() {
  const res = await axios.get(`${HETZNER_API_URL}/servers/${SERVER_ID}`, { headers });
  const server = res.data.server;
  return {
    id: "real-hetzner",
    nombre: "VPS Real",
    ip: server.public_net.ipv4.ip,
    estado: server.status === "running" ? "online" : "offline",
    cpu: "6.4%", // puedes cambiar esto con datos reales
    ram: "2651.0 / 15870.0 MB",
    servicios: ["Hosting", "Node.js", "React"],
  };
}

export async function encenderVpsReal() {
  await axios.post(`${HETZNER_API_URL}/servers/${SERVER_ID}/poweron`, null, { headers });
}

export async function apagarVpsReal() {
  await axios.post(`${HETZNER_API_URL}/servers/${SERVER_ID}/poweroff`, null, { headers });
}

export async function reiniciarVpsReal() {
  await axios.post(`${HETZNER_API_URL}/servers/${SERVER_ID}/reboot`, null, { headers });
}
